#' @title Create input table for descriptive table generated by descrTable
#'
#' @description Create input table for descriptive table generated by descrTable. This input
#' table defines the variables to report, the measures to apply and the
#' precision of the results.
#'
#' @details utility function for formating
#'
#' @param d.data Data frame to report statistics from
#' @param var.list A data frame for the mapping of the variable names to meaningful labels. The attribute 'var_name' for the variable names in d.data and 'var_label' for the labels.
#' @return data.frame as input for 'descrTable'
#' @return - measure_label: label text shown in resulting table (output from descrTable())
#' @return - measure_name:  variable name
#' @return - measure_1:  first statistics to be applied to date
#' @return - measure_2:  second statistics to be applied to date
#' @return - measure_ref_level:  for factors, specify the reference level
#' @return - measure_prec_1:  precision - number of decimal places - for first statistics
#' @return - measure_prec_2:  precision for second statistics
#' @note under continuous developement
#' @author Roland Rapold
#' @seealso other utility-functions in this R-package
#' @references none
#' @examples
#' if(require("car")) {
#'     # load sample data 'Mroz' from package 'car'
#'     data(Mroz)
#'     str(Mroz)
#'
#'     # insert attribute 'i' for population size
#'     Mroz <- merge(data.frame(i=as.factor(1)), Mroz)
#'     str(Mroz)
#'
#'     # reference list for variable labels
#'     var.list <- data.frame(var_name=c("i", "lfp", "k5", "k618", "wc", "lwg", "inc"),
#'                            var_label=c("population",
#'                                        "employed",
#'                                        "# children to 5", "# children 6-18",
#'                                        "wife college",
#'                                        "log expected wage",
#'                                        "family income excl. wife"))
#'
#'     # create definition-table without variable for groups
#'     Mroz_hc      <- Mroz[ , -which(colnames(Mroz)=="hc")]
#'
#'     # variation 1: default, no adjustments
#'     def.measures.1 <- createDefMeasures(d.data=Mroz_hc)
#'     print(def.measures.1)
#'
#'     # variation 2: apply variable labels
#'     def.measures.2 <- createDefMeasures(d.data=Mroz_hc, var.list=var.list)
#'     def.measures.2[2, 6] <- -2
#'     def.measures.2[9, 6] <- -1
#'     print(def.measures.2)
#'
#' }
#' @export
createDefMeasures <- function (d.data, var.list)
{
    #
    # d.data       = input-data for 'descrMeasures' and 'descrTable'
    # var.list     = list with variable names and labels
    #                var.list$var_name      ->  def.measures$measure_name
    #                var.list$var_label     ->  def.measures$measure_label
    # def.measures = result: input-parameter table for 'descrMeasures' and 'descrTable'
    #
    # local options ................................................................................
    options(stringsAsFactors=FALSE)
    #
    # pasteClass local function ....................................................................
    pasteClass <- function(x)
    {
        class.x <- class(x)
        if(length(class.x)>1)
        {
            class.x <- paste(class.x, collapse=" ")
        }
        return(class.x)
    }
    #
    # check input data                                  ............................................
    if (missing(d.data) || !is.data.frame(d.data))
    {
        return("please provide data.frame as 'd.data'")
    }
    #
    # create generic definition-table if none is provided ..........................................
  # if (missing(def.measures) || is.null(def.measures))
  # {
        def.measures <- data.frame(measure_label="a", measure_name="a"
                                 , measure_1="a"
                                 , measure_2="a"
                                 , measure_ref_level=NA
                                 , measure_prec_1=1
                                 , measure_prec_2=1)
        i.row        <- 1

        for(i in 1:ncol(d.data))
        {
            if(pasteClass(d.data[, i])=="numeric")
            {
                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "mean"
                def.measures[i.row, "measure_2"] <- "median"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "mean"
                def.measures[i.row, "measure_2"] <- "sd"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "median"
                def.measures[i.row, "measure_2"] <- "IQR"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "sum"
                def.measures[i.row, "measure_2"] <- NA
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- NA
                i.row <- i.row + 1
            }
            if(pasteClass(d.data[, i])=="integer")
            {
                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "mean"
                def.measures[i.row, "measure_2"] <- "median"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "mean"
                def.measures[i.row, "measure_2"] <- "sd"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "median"
                def.measures[i.row, "measure_2"] <- "IQR"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1

                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "sum"
                def.measures[i.row, "measure_2"] <- NA
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 1
                def.measures[i.row, "measure_prec_2"] <- NA
                i.row <- i.row + 1
            }
            if(pasteClass(d.data[, i])=="factor")
            {
                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "count"
                def.measures[i.row, "measure_2"] <- "portion"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 0
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1
            }
            if(pasteClass(d.data[, i])=="ordered factor")
            {
                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "count"
                def.measures[i.row, "measure_2"] <- "portion"
                def.measures[i.row, "measure_prec_1"] <- 0
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1
            }
            if(pasteClass(d.data[, i])=="ordered")
            {
                def.measures[i.row, "measure_label"] <- colnames(d.data)[i]
                def.measures[i.row, "measure_name"]  <- colnames(d.data)[i]
                def.measures[i.row, "measure_1"] <- "count"
                def.measures[i.row, "measure_2"] <- "portion"
                def.measures[i.row, "measure_ref_level"] <- NA
                def.measures[i.row, "measure_prec_1"] <- 0
                def.measures[i.row, "measure_prec_2"] <- 1
                i.row <- i.row + 1
            }
        }
  # }
  # else {
  #     return(def.measures)
  # }
    #
    # substitute variable labels from reference list    ............................................
    if (!missing(var.list) && !is.null(var.list))
    {
        # order of rows
        def.measures$nr <- 1:nrow(def.measures)

        # merge reference list for measure_label's
        def.measures <- merge(def.measures[, -which(colnames(def.measures) == "measure_label")]
                              , var.list[, c("var_label", "var_name")]
                              , by.x = "measure_name"
                              , by.y = "var_name"
                              , all.x = TRUE)

        # get default labels when missing from reference list
        missing_label <- which(is.na(def.measures$var_label) | def.measures$var_label=="")
        def.measures[missing_label, "var_label"] <- 
        def.measures[missing_label, "measure_name"]
        # def.measures[is.na(def.measures$var_label), "var_label"] <-
        # def.measures[is.na(def.measures$var_label), "measure_name"]
        # def.measures[def.measures$var_label=="", "var_label"] <-
        # def.measures[def.measures$var_label=="", "measure_name"]

        # apply original order of rows
        def.measures <- def.measures[order(def.measures$nr), ]
        def.measures$nr <- NULL

        # apply and name original column order
        def.measures <- def.measures[, c("var_label", "measure_name", "measure_1", "measure_2",
                                         "measure_ref_level", "measure_prec_1", "measure_prec_2")]
        colnames(def.measures)[1] <- "measure_label"
    }
    #
    return(def.measures)
}
# --------------- createDefMeasures -------------------------------------------------------------- --
# END OF FUNCTION ------------------------------------------------------------------------------- --
## }}}
# --------------- descrMeasures                  calculation of statistics for one entry          .. # {{{
# RR 20150325     ------------------------------------------------------------------------------- --
#
# Possible characteristics:
#   - count             factor or Value with 1 value (Auspraegung) (<- noch zu kontrollieren!!)
#   - count_distinct    factor
#   - portion           factor -- vertical 100%, otherwise horizontal 100%
#   - mean              numeric
#   - wtd_mean          numeric
#   - sd                numeric
#   - median            numeric
#   - wtd_median        numeric
#   - IQR               numeric
#   - sum               numeric
# Second characteristics after the first
#       - portion       factor -- vertical 100%, otherwise horizontal 100%
#       - mean          numerisch
#       - wtd_mean      numerisch
#       - sd            numerisch
#       - median        numerisch
#       - wtd_median    numerisch
#       - IQR           numerisch
#
# special case for variable 'i' (factor 1) --> interpreted as record entry for group size
#
descrMeasures  <- function(descr.table=NULL             # result table to append to
                           , var.measure.label          # variable label
                           , var.measure.name           # variable name
                           , measures                   # measures
                           , measure.ref.level          # reference level for factors
                           , prec.digit                 # digital precision
                           , sub.d1                     # data column 1
                           , sub.d2                     # data column 2
                           , sub.d3                     # data column 3
                           , sub.d4                     # data column 4
                           , sub.d5                     # data column 5
                           , weights.1                  # weights (data column 1)
                           , weights.2                  # weights (data column 2)
                           , weights.3                  # weights (data column 3)
                           , weights.4                  # weights (data column 4)
                           , weights.5                  # weights (data column 5)
                           , label.compact              # combine variable label and measure
                           , label.width                # width of 1st column (only if label.compact==TRUE)
                           , test.gr                    # data columns to test
                           , lang                       # language 'de' or 'en'
                           , group.size.total           # data column containing the ttotal (if any)
                                                        # 0 (default) = no total column defined
                           , big.mark
                           , only.first.label
                           , args.with.na
                           , verbose)                   # 0 bis 2: increasing verbosity
{
    # local options ................................................................................
    options(stringsAsFactors=FALSE)
    #
    # ------------------------------------------------------------------------------------------- --
    #  Definition lokaler Funktionen                                                              ..
    trim.loc <- function (x) gsub("^\\s+|\\s+$", "", x)
    #
    # ------------------------------------------------------------------------------------------- --
    if(verbose>1)
    {
        print(paste("var.measure.label=", var.measure.label))
        print(paste("var.measure.name=", var.measure.name))
        print(paste("measures=", measures))
        print(paste("measure.ref.level=", measure.ref.level))
        print(paste("prec.digit=", prec.digit))
        print(paste("test.gr=", test.gr))
    }
    # ------------------------------------------------------------------------------------------- --
    # withWarnings() local function ................................................................ # {{{
    withWarnings <- function(expr) {
        myWarnings <- NULL
        wHandler <- function(w) {
            myWarnings <<- c(myWarnings, list(w))
            invokeRestart("muffleWarning")
        }
        val <- withCallingHandlers(expr, warning = wHandler)
        list(value = val, warnings = myWarnings)
    }
    ## }}}
    # ------------------------------------------------------------------------------------------- --
    # 0. check the measures and combination of measures                                           .. # {{{
    # xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx x
    #
    #    first measure
    if(!measures[1]%in%c("count", "count_distinct", "portion", "mean", "wtd_mean", "sd", "median",
                         "wtd_median", "IQR", "min", "max", "sum"))
    {
        return(print(paste("selected first measure is not valid:", measures[1])))
    }
    #
    #    second measure
    if(length(measures)>1 & !is.na(measures[2]))
    {
        if(!measures[2]%in%c("portion", "mean", "wtd_mean", "sd", "median",
                             "wtd_median", "IQR", "min", "max", "sum"))
        {
            return(print(paste("selected second measure ist not valid:", measures[2])))
        }
        if(measures[2]=="portion" & !measures[1]=="count")
            return(print(paste("if the second measure is \'portion\' the first has to be \'count\'!")))
    }
    #
    # vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv
    ## }}}
    # ------------------------------------------------------------------------------------------- --
    # 0. preliminaries                                                                            .. # {{{
    # xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx x
    #
    # default value for p-Value ....................................................................
    p.na <- "- "
    #
    # indication flags for test statistics .........................................................
    i.chisq         <- 0
    i.wilcoxon      <- 0
    i.kruskal       <- 0
    i.test.warning  <- 0
    #
    # combind measure vector .......................................................................
    ifelse(is.na(measures[2]), measures <- c(measures[1]),
                               measures <- c(measures[1], measures[2]))
    #
    # default flag to unweighted statistics ........................................................
    i.weights   <- 0                # 0: unweighted statistics
                                    # 1: weighted statistics
    #                               # is overwritten by wtd_mean or wtd_median
    #
    # get number of data dimensions ................................................................
    dim.data <- 1
    if(!is.null(sub.d2)) dim.data <- 2
    if(!is.null(sub.d3)) dim.data <- 3
    if(!is.null(sub.d4)) dim.data <- 4
    if(!is.null(sub.d5)) dim.data <- 5
    if(verbose>1) print(paste("dim.data: -- :", dim.data))
    #
    # create data vectors ..........................................................................
                   sub.data.1 <- sub.d1[, var.measure.name]
    if(dim.data>1) sub.data.2 <- sub.d2[, var.measure.name]
    if(dim.data>2) sub.data.3 <- sub.d3[, var.measure.name]
    if(dim.data>3) sub.data.4 <- sub.d4[, var.measure.name]
    if(dim.data>4) sub.data.5 <- sub.d5[, var.measure.name]
    rm(sub.d1, sub.d2, sub.d3, sub.d4, sub.d5)
    #
    # check occurrences of NAs .....................................................................
                   res.na <- c(        sum(is.na(sub.data.1)))
    if(dim.data>1) res.na <- c(res.na, sum(is.na(sub.data.2)))
    if(dim.data>2) res.na <- c(res.na, sum(is.na(sub.data.3)))
    if(dim.data>3) res.na <- c(res.na, sum(is.na(sub.data.4)))
    if(dim.data>4) res.na <- c(res.na, sum(is.na(sub.data.5)))
    #
    # check variable type - factor vs. numeric .....................................................
    var.is.factor <- is.factor(sub.data.1)      # && !"count"%in%measures
    if(verbose>1) print(paste("var.is.factor: -- :", var.is.factor))
    #
    # prepare for factors ..........................................................................
    if(var.is.factor & !is.na(measure.ref.level))
    {
        if(!measure.ref.level%in%levels(sub.data.1)) {
            return(print(paste("selected reference group 'measure.ref.level' is not valid !",
                                var.measure.label)))
        }
        if(measure.ref.level!=levels(sub.data.1)[1])
        {
                           sub.data.1 <- stats::relevel(sub.data.1, measure.ref.level)
            if(dim.data>1) sub.data.2 <- stats::relevel(sub.data.2, measure.ref.level)
            if(dim.data>2) sub.data.3 <- stats::relevel(sub.data.3, measure.ref.level)
            if(dim.data>3) sub.data.4 <- stats::relevel(sub.data.4, measure.ref.level)
            if(dim.data>4) sub.data.5 <- stats::relevel(sub.data.5, measure.ref.level)
        }
    }
    #
    # control -- no factor level empty .......................................................... ..
    if(var.is.factor)
    {
        # if(requireNamespace("DescTools", quietly=TRUE))
        # {
                           levels(sub.data.1) <- trim.loc(levels(sub.data.1))
            if(dim.data>1) levels(sub.data.2) <- trim.loc(levels(sub.data.2))
            if(dim.data>2) levels(sub.data.3) <- trim.loc(levels(sub.data.3))
            if(dim.data>3) levels(sub.data.4) <- trim.loc(levels(sub.data.4))
            if(dim.data>4) levels(sub.data.5) <- trim.loc(levels(sub.data.5))
            # }
        m <- which(levels(sub.data.1)=="")
        if(length(m)>0)
        {
            if(lang=="de")
            {
                               levels(sub.data.1)[m] <- "_leer_"
                if(dim.data>1) levels(sub.data.2)[m] <- "_leer_"
                if(dim.data>2) levels(sub.data.3)[m] <- "_leer_"
                if(dim.data>3) levels(sub.data.4)[m] <- "_leer_"
                if(dim.data>4) levels(sub.data.5)[m] <- "_leer_"
            } else {
                               levels(sub.data.1)[m] <- "_empty_"
                if(dim.data>1) levels(sub.data.2)[m] <- "_empty_"
                if(dim.data>2) levels(sub.data.3)[m] <- "_empty_"
                if(dim.data>3) levels(sub.data.4)[m] <- "_empty_"
                if(dim.data>4) levels(sub.data.5)[m] <- "_empty_"
            }
        }
        rm(m)
    }
    #
    # control -- no numbers as factor levels .......................................................
    if(var.is.factor &&
       sum(levels(sub.data.1)%in%c(0:10000))==length(levels(sub.data.1)) &&
       var.measure.name!="i")
    {
                       levels(sub.data.1) <- paste(levels(sub.data.1), "_", sep="")
        if(dim.data>1) levels(sub.data.2) <- paste(levels(sub.data.2), "_", sep="")
        if(dim.data>2) levels(sub.data.3) <- paste(levels(sub.data.3), "_", sep="")
        if(dim.data>3) levels(sub.data.4) <- paste(levels(sub.data.4), "_", sep="")
        if(dim.data>4) levels(sub.data.5) <- paste(levels(sub.data.5), "_", sep="")
    }
    #
    # control -- only portion and count are valid for factors ......................................
    if(var.is.factor)
    {
        not_m1 <- !measures[1]%in%c("portion", "count", "count_distinct")
        not_m2 <- ifelse(length(measures)>1, !measures[2]%in%c("portion", "count", "count_distinct"), FALSE)
        if(not_m1 || not_m2)
        {
            return(paste("for factors only 'portion' or 'count' or 'count_distinct' as measures are permitted!\n",
                         "class of the provided variable:", class(sub.data.1), "\n",
                         "selected measures:", measures))
        }
    }
    if(verbose>1) print("Schritt 01")
    if(verbose>1) print(paste("variable : ---- :", var.measure.name))
    if(verbose>1) print(paste("measures : ---- :", measures))
    #
    # vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv
    ## }}}
    # ------------------------------------------------------------------------------------------- --
    # calculate 1. statistics
    # ------------------------------------------------------------------------------------------- --
    # Number of occurrence                              'count'                                   .. # {{{
    # xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx x
    #
    # NAs not accounted for !
    #
    if(measures[1]=="count")
    {
      # str(sub.data.1)
      # sub.data.1 <- rep(1, 100)
      # sub.data.2 <- rep(2, 100)
      # sub.data.3 <- rep(3, 100)

        if(dim.data==1)
        {
            measure.1 <- data.frame(table(sub.data.1))
            measure.1 <- measure.1[, c(1, 2)]
        }

        if(dim.data==2)
        {
            measure.1 <- data.frame(table(sub.data.1),
                                    table(sub.data.2))
            measure.1 <- measure.1[, c(1, 2, 4)]
        }
        if(dim.data==3)
        {
            measure.1 <- data.frame(table(sub.data.1),
                                    table(sub.data.2),
                                    table(sub.data.3))
            measure.1 <- measure.1[, c(1, 2, 4, 6)]
        }
        if(dim.data==4)
        {
            measure.1 <- data.frame(table(sub.data.1),
                                    table(sub.data.2),
                                    table(sub.data.3),
                                    table(sub.data.4))
            measure.1 <- measure.1[, c(1, 2, 4, 6, 8)]
        }
        if(dim.data==5)
        {
            measure.1 <- data.frame(table(sub.data.1),
                                    table(sub.data.2),
                                    table(sub.data.3),
                                    table(sub.data.4),
                                    table(sub.data.5))
            measure.1 <- measure.1[, c(1, 2, 4, 6, 8, 10)]
        }

        measure.1[, 1] <- paste("'", levels(sub.data.1), "'", sep="")
        label.1   <- ifelse(lang=="de", "N", "n")

        if(verbose>1) print(paste("count - measure.1: -- :", measure.1))
        if(verbose>1) print(paste("count - label.1: -- :", label.1))
    }
    #
    # vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv
    ## }}}
    # Number of differen occurrences                    'count_distinct'                          .. # {{{
    # xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx xx x
    #
    # NAs accounted for !
    #
